function reverse(x) {
    // Butun sonni teskari aylantiring
    const sign = x < 0 ? -1 : 1;
    x = Math.abs(x);
    let result = 0;
    while (x > 0) {
        result = result * 10 + x % 10;
        x = Math.floor(x / 10);
    }
    result *= sign;
    return (result < -(2**31) || result > 2**31 - 1) ? 0 : result;
}

