codeTemplates.python:
def factorial(n):
    # n ning faktorialini hisoblang
    if n == 0:
        return 1
    result = 1
    for i in range(1, n + 1):
        result *= i
    return result

codeTemplates.java:
public class Solution {
    public int factorial(int n) {
        // n ning faktorialini hisoblang
        if (n == 0) return 1;
        int result = 1;
        for (int i = 1; i <= n; i++) {
            result *= i;
        }
        return result;
    }
}

